t($params['type']) && in_array($params['type'],$recognisedtypes )) { $classValidate.="custom[".$params['type']."]"; } } if($prefixclass) $prefixclass.=" "; if($classValidate) $classValidate="validate[".$classValidate."]"; if(!$returnAttr && $classValidate) $classValidate= ' class="'.$prefixclass.$classValidate.'" '; return $classValidate; } } 