<?php
defined('WYSIJA') or die('Restricted access'); class WYSIJA_help_back extends WYSIJA_help{ function WYSIJA_help_back(){ parent::WYSIJA_help(); $config=&WYSIJA::get("config","model"); $linkcontent=__("It doesn't always work the way we want it to, doesn't it? We have a [link]dedicated support website[/link] with documentation and a ticketing system.",WYSIJA); $finds=array("[link]",'[/link]'); $replace=array('<a target="_blank" href="http://support.wysija.com" title="support.wysija.com">','</a>'); $truelinkhelp=str_replace($finds,$replace,$linkcontent); $truelinkhelp.="<p>".__("Wysija Version: ",WYSIJA)."<strong>".WYSIJA::get_version()."</strong></p>"; $this->menus=array( "campaigns"=>array("title"=>__("Wysija",WYSIJA),"help"=>$truelinkhelp), "subscribers"=>array("title"=>__("Subscribers",WYSIJA),"help"=>$truelinkhelp), "config"=>array("title"=>__("Settings",WYSIJA),"help"=>$truelinkhelp), ); if($config->getValue("debug_on")) include_once(WYSIJA_INC."debug.php"); if(isset($_GET['page']) && substr($_GET['page'],0,7)=="wysija_"){ define("WYSIJA_ITF",TRUE); if(WYSIJA_DBG===true){ error_reporting(E_ALL); ini_set('display_errors', '1'); }else{ error_reporting(0); ini_set('display_errors', '0'); } $this->controller=&WYSIJA::get(str_replace("wysija_","",$_GET['page']),"controller"); }else{ define("WYSIJA_ITF",FALSE); if(defined('WYSIJA_DBG_ALL')){ error_reporting(E_ALL); ini_set('display_errors', '1'); } } if(defined('DOING_AJAX')){ if(!isset($_REQUEST['adminurl']) && !isset($_REQUEST['wysilog'])) add_action('wp_ajax_nopriv_wysija_ajax', array($this, 'ajax')); else add_action('wp_ajax_wysija_ajax', array($this, 'ajax')); }else{ if(WYSIJA_ITF) { add_action('admin_init', array($this->controller, 'main')); add_action('admin_footer',array($this,'version'),9); if( !$config->getValue("sending_emails_ok")){ $msg=$config->getValue("ignore_msgs"); $urlsendingmethod='admin.php?page=wysija_config#sendingmethod'; if($_REQUEST['page']=='wysija_config'){ $urlsendingmethod="#sendingmethod"; } global $wysija_installing; if(!isset($msg['setupmsg']) && $wysija_installing!==true){ $this->notice(str_replace(array("[link_widget]","[link_ignore]","[link]","[/link]","[/link_widget]","[/link_ignore]"), array('<a href="widgets.php">','<a class="linkignore setupmsg" href="javascript:;">','<a id="linksendingmethod" href="'.$urlsendingmethod.'">','</a>','</a>','</a>'), __('Hurray! Add a form to your site using [link_widget]the Widget[/link_widget] and confirm your site can send emails in the [link]Settings[/link]. [link_ignore]Ignore[/link_ignore].',WYSIJA)),true); } } } add_action('admin_menu', array($this, 'add_menus'),99); add_action('admin_enqueue_scripts',array($this, 'add_js'),10,1); if(defined('DISABLE_WP_CRON') && DISABLE_WP_CRON) $this->wp_notice(__('The CRON system is disabled on your wordpress site. Wysija will not work correctly while it stays disabled.',WYSIJA)); $importPossible=$config->getValue("pluginsImportable"); if(!$config->getValue("pluginsImported") && $importPossible){ foreach($importPossible as $tableName =>$pluginInfos){ if((isset($_REQUEST['action']) && $_REQUEST['action']!="importplugins") || !isset($_REQUEST['action'])) $this->notice( str_replace(array("[link]","[/link]"), array('<a href="admin.php?page=wysija_subscribers&action=importplugins">','</a>'), sprintf(__('The plugin %1$s has been found. If you wish to import into Wysija the %2$s subscribers from that plugin click [link]here[/link].',WYSIJA),'<strong>"'.$pluginInfos['name'].'"</strong>',$pluginInfos['total']))); } } add_action('admin_head-post-new.php',array($this,'addCodeToPagePost')); add_action('admin_head-post.php',array($this,'addCodeToPagePost')); } } function add_contextual_help(){ foreach($this->menus as $action=> $menu){ $actionFull='wysija_'.$action; add_contextual_help($actionFull, $menu['help'] ); } } function add_menus(){ $modelC=&WYSIJA::get("config","model"); $count=0; global $menu,$submenu; $position=50; while(isset($menu[$position])){ $position++; } global $wysija_installing; foreach($this->menus as $action=> $menutemp){ $actionFull='wysija_'.$action; if(!isset($menutemp['subtitle'])) $menutemp['subtitle']=$menutemp['title']; if($action=='campaigns') $rolecampaign=$modelC->getValue('role_campaign'); else $rolecampaign='manage_options'; if($wysija_installing===true){ if($count==0){ $parentmenu=$actionFull; $hookname=add_menu_page($menutemp['title'], $menutemp['subtitle'], $rolecampaign, $actionFull , array($this->controller, 'errorInstall'), WYSIJA_EDITOR_IMG.'mail.png', $position); } }else{ if($count==0){ $parentmenu=$actionFull; $hookname=add_menu_page($menutemp['title'], $menutemp['subtitle'], $rolecampaign, $actionFull , array($this->controller, 'render'), WYSIJA_EDITOR_IMG.'mail.png', $position); }else{ $hookname=add_submenu_page($parentmenu,$menutemp['title'], $menutemp['subtitle'], $rolecampaign, $actionFull , array($this->controller, 'render')); } if(WYSIJA_ITF) add_contextual_help($hookname, $menutemp['help'] ); } $count++; } if(isset($submenu[$parentmenu])) $submenu[$parentmenu][0][0]=$submenu[$parentmenu][0][3]=__('Newsletters',WYSIJA); } function add_js($hook) { wp_enqueue_script('wysija-admin', WYSIJA_URL."js/admin.js", array( 'jquery' ), true); $jstrans=array(); wp_register_script('wysija-charts', "https://www.google.com/jsapi", array( 'jquery' ), true); wp_register_script('wysija-admin-list', WYSIJA_URL."js/admin-listing.js", array( 'jquery' ), true); wp_register_script('wysija-base64', WYSIJA_URL."js/base64.js", array( 'jquery' ), true); if(WYSIJA_ITF){ $pagename=str_replace("wysija_","",$_REQUEST['page']); $backloader=&WYSIJA::get("backloader","helper"); $backloader->initLoad($this->controller); $jstrans=$this->controller->jsTrans; $jstrans['gopremium']=__("Go Premium Now!"); $backloader->jsParse($this->controller,$pagename,WYSIJA_URL); $backloader->loadScriptsStyles($pagename,WYSIJA_DIR,WYSIJA_URL,$this->controller); } $jstrans["newsletters"]=__('Newsletters',WYSIJA); wp_localize_script('wysija-admin', 'wysijatrans', $jstrans); } function addCodeToPagePost(){ if ( get_user_option('rich_editing') == 'true') { add_filter("mce_external_plugins", array($this,"addRichPlugin")); add_filter('mce_buttons', array($this,'addRichButton1'),999); add_filter('tiny_mce_before_init', array($this,'TMCEinnercss'),12 ); wp_enqueue_style('custom_TMCE_admin_css', WYSIJA_URL.'css/tmce/panelbtns.css'); wp_print_styles('custom_TMCE_admin_css'); } } function addRichPlugin($plugin_array) { $plugin_array['wysija_register'] = WYSIJA_URL.'mce/wysija_register/editor_plugin.js'; return $plugin_array; } function addRichButton1($buttons) { $newButtons=array(); foreach($buttons as $value) $newButtons[]=$value; array_push($newButtons, "|", "wysija_register"); return $newButtons; } function TMCEinnercss($init) { $myStyleUrl = WYSIJA_URL."css/tmce/style.css"; $init['content_css'] = $myStyleUrl; return $init; } function version(){ echo "<div class='wysija-version'>"; echo __("Wysija Version: ",WYSIJA)."<strong>".WYSIJA::get_version()."</strong>"; echo "</div>"; } } 